public class Heap {
    private Heap() {
    }

    public static void sort(Comparable[] pq) {
        int n = pq.length;
        for (int k = n / 2; k >= 1; k--) {
            sink(pq, k, n);// æ„é€ å †
        }
        while (n > 1) {
            exch(pq, 1, n--);// å°†æ•°ç»„æœ€å¤§å…ƒç´ å’Œæœ«å°¾å…ƒç´ äº¤æ¢ï¼Œç„¶ååˆ é™¤
            sink(pq, 1, n);// ä¿æŒå †æœ‰åº
        }
    }

    private static void sink(Comparable[] pq, int k, int n) {
        while (2 * k <= n) {
            int j = 2 * k;
            if (j < n && less(pq, j, j + 1))
                j++;
            if (!less(pq, k, j))
                break;
            exch(pq, k, j);
            k = j;
        }
    }

    // helper functions
    private static boolean less(Comparable[] pq, int i, int j) {
        return pq[i - 1].compareTo(pq[j - 1]) < 0;
    }

    private static void exch(Object[] pq, int i, int j) {
        Object t = pq[i - 1];
        pq[i - 1] = pq[j - 1];
        pq[j - 1] = t;
    }
}
