import java.util.NoSuchElementException;
import java.util.Iterator;

public class IndexMaxPQ<Key extends Comparable<Key>> implements Iterable<Integer> {
    private int n;// pqä¸­çš„å…ƒç´ æ•°é‡
    private int[] pq;// ä¼˜å…ˆé˜Ÿåˆ—ï¼Œå…¶ä¸­å­˜å‚¨çš„æ˜¯å…ƒç´ åœ¨Keyæ•°ç»„ä¸­çš„ç´¢å¼•
    // keyæ•°ç»„ç´¢å¼•å¯¹åº”çš„å…ƒç´ ï¼Œåœ¨ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„ç´¢å¼•ä½ç½®,
    // åœ¨keysæ•°ç»„ä¸­çš„å…ƒç´ è¢«æ”¹å˜æ—¶ï¼Œå¿«é€Ÿæ‰¾åˆ°è¯¥å…ƒç´ åœ¨ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„ä½ç½®ï¼Œè¿›è¡Œä¸Šæµ®å’Œä¸‹æ²‰æ“ä½œä»¥ä¿æŒå †æœ‰åº
    private int[] qp;
    private Key[] keys;// å®é™…å­˜æ”¾å…ƒç´ çš„æ•°ç»„

    // initializes
    public IndexMaxPQ(int maxN) {
        if (maxN < 0)
            throw new IllegalArgumentException();
        n = 0;
        keys = (Key[]) new Comparable[maxN + 1];
        pq = new int[maxN + 1];
        qp = new int[maxN + 1];
        for (int i = 0; i <= maxN; i++) {
            qp[i] = -1;
        }
    }

    public boolean isEmpty() {
        return n == 0;
    }

    public boolean contains(int i) {
        return qp[i] != -1;
    }

    public int size() {
        return n;
    }

    public void insert(int i, Key key) {// æ’å…¥
        if (contains(i))
            throw new NoSuchElementException("index is already in the priority queue");
        n++;
        qp[i] = n;// keys[i]åœ¨ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„ç´¢å¼•
        pq[n] = i;// ä¼˜å…ˆé˜Ÿåˆ—ç´¢å¼•nå¤„å­˜æ”¾å…ƒç´ åœ¨keys[]ä¸­çš„ç´¢å¼•
        keys[i] = key;// å­˜æ”¾æ•°æ®
        swin(n);// ä¸Šæµ®
    }

    public int MaxIndex() {// è¿”å›æœ€å¤§å…ƒç´ ç´¢å¼•
        if (n == 0)
            throw new NoSuchElementException("priority queue underflow");
        return pq[1];
    }

    public Key maxKey() {// è¿”å›æœ€å¤§å…ƒç´ 
        if (n == 0)
            throw new NoSuchElementException("priority queue underflow");
        return keys[pq[1]];
    }

    public int delMax() {// åˆ é™¤æœ€å¤§å€¼
        if (n == 0)
            throw new NoSuchElementException("Priority queue underflow");
        int max = pq[1];
        exch(1, n--);
        sink(1);

        assert pq[n + 1] == max;
        qp[max] = -1;
        keys[max] = null;
        pq[n + 1] = -1;
        return max;
    }

    public Key keyOf(int i) {// è¿”å›iå¯¹åº”çš„å€¼
        if (!contains(i))
            throw new NoSuchElementException("index is not in the priority queue");
        else
            return keys[i];
    }

    public void changeKey(int i, Key key) {// ä¿®æ”¹iä½ç½®çš„å€¼ä¸ºkey
        if (!contains(i))
            throw new NoSuchElementException("index is not in the priority queue");
        keys[i] = key;
        swin(qp[i]);// åœ¨ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„ä½ç½®
        sink(qp[i]);
    }

    public void increaseKey(int i, Key key) {// ä¿®æ”¹iä½ç½®çš„å€¼ä¸ºkey keyå¤§äºåŸå€¼
        if (!contains(i))
            throw new NoSuchElementException("index is not in the priority queue");
        if (keys[i].compareTo(key) >= 0)
            throw new IllegalArgumentException(
                    "Calling increaseKey() with given argument would not strictly increase the key");
        keys[i] = key;
        swin(qp[i]);// åœ¨ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„ä½ç½®
    }

    public void decreaseKey(int i, Key key) {// ä¿®æ”¹iä½ç½®çš„å€¼ä¸ºkey keyå°äºåŸå€¼
        if (!contains(i))
            throw new NoSuchElementException("index is not in the priority queue");
        if (keys[i].compareTo(key) <= 0)
            throw new IllegalArgumentException(
                    "Calling decreaseKey() with given argument would not strictly decrease the key");
        keys[i] = key;
        sink(qp[i]);// åœ¨ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„ä½ç½®
    }

    public void delete(int i) {// åˆ é™¤iä½ç½®çš„å…ƒç´ 
        if (!contains(i))
            throw new NoSuchElementException("inde is not in the priority queue");
        int index = qp[i];
        exch(index, n--);
        swin(index);
        sink(index);
        keys[i] = null;
        qp[i] = -1;
    }
    // helper functions

    private boolean less(int i, int j) {
        return keys[pq[i]].compareTo(keys[pq[j]]) < 0;
    }

    private void exch(int i, int j) {
        int t = pq[i];
        pq[i] = pq[j];
        pq[j] = t;
        qp[pq[i]] = i;
        qp[pq[j]] = j;
    }

    private void swin(int k) {
        while (k > 1 && less(k / 2, k)) {
            exch(k / 2, k);
            k /= 2;
        }
    }

    private void sink(int k) {
        while (2 * k < n) {
            int j = 2 * k;
            if (j < n && less(j, j + 1))
                j++;
            if (!less(k, j))
                break;
            exch(k, j);
            k = j;
        }
    }

    public Iterator<Integer> iterator() {
        return new HeapIterator();
    }

    private class HeapIterator implements Iterator<Integer> {

        private IndexMaxPQ<Key> copy;

        public HeapIterator() {
            copy = new IndexMaxPQ<Key>(pq.length - 1);
            for (int i = 1; i <= n; i++)
                copy.insert(pq[i], keys[pq[i]]);
        }

        public boolean hasNext() {
            return !copy.isEmpty();
        }

        public void remove() {
            throw new UnsupportedOperationException();
        }

        public Integer next() {
            if (!hasNext())
                throw new NoSuchElementException();
            return copy.delMax();
        }
    }
}
