import java.util.Comparator;
import java.util.Iterator;
import java.util.NoSuchElementException;

public class MaxPQ<Key> implements Iterable<Key> {
    private Key[] pq; // å­˜å‚¨å…ƒç´ çš„æ•°ç»„
    private int n; // ä¼˜å…ˆé˜Ÿåˆ—ä¸­çš„å…ƒç´ æ•°é‡
    private Comparator<Key> comparator;

    // Construction functions

    public MaxPQ(int initCapacity) {
        pq = (Key[]) new Object[initCapacity + 1];
        n = 0;
    }

    public MaxPQ() {
        this(1);
    }

    public MaxPQ(int initCapacity, Comparator<Key> comparator) {
        this.comparator = comparator;
        pq = (Key[]) new Object[initCapacity + 1];
        n = 0;
    }

    public MaxPQ(Comparator<Key> comparator) {
        this(1, comparator);
    }

    public MaxPQ(Key[] keys) {
        n = keys.length;
        pq = (Key[]) new Object[keys.length + 1];
        for (int i = 0; i < n; i++) {
            pq[i + 1] = keys[i];
        }
        for (int k = n / 2; k >= 1; k--) {
            sink(k);
        }
        assert isMaxHeap();
    }

    public boolean isEmpty() {
        return n == 0;
    }

    public int size() {
        return n;
    }

    public Key max() {
        if (isEmpty())
            throw new NoSuchElementException("Priority queue underflow");
        return pq[1];
    }

    private void resize(int capacity) {//è°ƒæ•´é˜Ÿåˆ—é•¿åº¦
        assert capacity > n;
        Key[] t = (Key[]) new Object[capacity];
        for (int i = 1; i <= n; i++) {
            t[i] = pq[i];
        }
        pq = t;
    }

    public void insert(Key x) {
        if(n==pq.length-1) resize(2*pq.length);
        pq[++n] = x;//å°†èŠ‚ç‚¹æ”¾åœ¨æ•°ç»„çš„æœ€å
        swin(n);//ä¸Šæµ®èŠ‚ç‚¹ï¼Œä»¥ä¿æŒå †æœ‰åº
        assert isMaxHeap();
    }

    public Key delMax() {
        if (isEmpty())
            throw new NoSuchElementException("Priority queue underflow");
        Key max = pq[1]; // è·å–ä¼˜å…ˆé˜Ÿåˆ—çš„æœ€å¤§å…ƒç´ 
        exch(1, n--);// å°†æ•°ç»„æœ€åçš„ä¸€ä¸ªå­èŠ‚ç‚¹ä¸æ ¹èŠ‚ç‚¹äº¤æ¢, ä¹‹åå‡å°nçš„å¤§å°ï¼Œå³åˆ é™¤äº†æœ€å¤§å…ƒç´ 
        sink(1);// ä¸‹æ²‰äº¤æ¢åçš„èŠ‚ç‚¹ï¼Œä½¿æ•°ç»„è¾¾åˆ°å †æœ‰åº
        pq[n + 1] = null;// é˜²æ­¢åˆ é™¤çš„èŠ‚ç‚¹ æ¸¸ç¦» å’Œ åƒåœ¾å›æ”¶
        if ((n > 0) && (n == (pq.length - 1) / 4))
            resize(pq.length / 2);// å‡å°æ•°ç»„çš„å¤§å° å‡å°å†…å­˜å ç”¨
        assert isMaxHeap();
        return max;
    }

    // helper functions
    private void swin(int k) {//ä¸Šæµ®èŠ‚ç‚¹
        while (k > 1 && less(k / 2, k)) {//æ¯”è¾ƒèŠ‚ç‚¹ä¸çˆ¶èŠ‚ç‚¹çš„å¤§å°
            exch(k, k / 2);//äº¤æ¢
            k /= 2;
        }
    }

    private void sink(int k) {//ä¸‹æ²‰
        while (2 * k <= n) {
            int j = 2 * k;
            if (j < n && less(j, j + 1))//æ¯”è¾ƒä¸¤ä¸ªå­èŠ‚ç‚¹çš„å¤§å°
                j++;
            if (!less(k, j))//å½“ ç´¢å¼•kä½ç½®çš„èŠ‚ç‚¹å¤§äºä¸¤ä¸ªå­—èŠ‚ç‚¹æ—¶ï¼Œä¸­æ–­
                break;
            exch(k, j);//å¦åˆ™äº¤æ¢
            k = j;
        }
    }

    private boolean less(int i, int j) {
        if (comparator == null) {
            return ((Comparable<Key>) pq[i]).compareTo(pq[j]) < 0;
        } else {
            return comparator.compare(pq[i], pq[j]) < 0;
        }
    }

    private void exch(int i, int j) {
        Key t = pq[i];
        pq[i] = pq[j];
        pq[j] = t;
    }

    public boolean isMaxHeap() {
        return isMaxHeap(1);
    }

    private boolean isMaxHeap(int k) {
        if (k > n)
            return true;
        int left = 2 * k;
        int right = 2 * k + 1;
        if (left <= n && less(k, left))
            return false;
        if (right <= n && less(k, right))
            return false;
        return isMaxHeap(left) && isMaxHeap(right);
    }

    public Iterator<Key> iterator() {
        return new HeapIterator();
    }

    private class HeapIterator implements Iterator<Key> {
        private MaxPQ<Key> copy;

        public HeapIterator() {
            if (comparator == null)
                copy = new MaxPQ<Key>(size());
            else
                copy = new MaxPQ<Key>(size(), comparator);
            for (int i = 1; i <= n; i++) {
                copy.insert(pq[i]);
            }
        }

        public boolean hasNext() {
            return !copy.isEmpty();
        }

        public void remove() {
            throw new UnsupportedOperationException();
        }

        public Key next() {
            if (!hasNext())
                throw new NoSuchElementException();
            return copy.delMax();
        }
    }
}
