public class Quick3way {

    private Quick3way() {
    }

    private static void sort(Comparable[] a) {
        StdRandom.shuffle(a);//éšæœºåŒ–æ•°ç»„å…ƒç´ ï¼Œä»¥é˜²æ­¢åˆ‡åˆ†ä¸å‡è¡¡
        sort(a, 0, a.length);
        assert isSorted(a);
    }

    private static void sort(Comparable[] a, int lo, int hi) {
        if (hi <= lo)
            return;
        int lt = lo, gt = hi;// ltæŒ‡é’ˆ ä»å·¦å¾€å³ç§»åŠ¨ græŒ‡é’ˆä»å³å¾€å·¦ç§»åŠ¨
        Comparable v = a[lo];// åˆ‡åˆ†å…ƒç´ 
        int i = lo + 1;
        while (i <= gt) {
            int cmp = a[i].compareTo(v);// å°†æ•°ç»„å‰©ä½™çš„å…ƒç´ å’Œvè¿›è¡Œæ¯”è¾ƒ
            if (cmp < 0)// å°äºçš„äº¤æ¢ltå’Œiå¤„çš„å…ƒç´ ï¼Œå¹¶ç§»åŠ¨æŒ‡é’ˆ
                exch(a, lt++, i++);
            else if (cmp > 0)// å¤§äºçš„äº¤æ¢iå’Œgtå¤„çš„å…ƒç´ ï¼Œå¹¶ç§»åŠ¨æŒ‡é’ˆ
                exch(a, i, gt--);
            else
