public class Merge {
    private Merge() {
    }

    // åŸåœ°å½’å¹¶çš„æŠ½è±¡æ–¹æ³•
    private static void merge(Comparable[] a, Comparable[] aux, int lo, int mid, int hi) {
        assert isSorted(a, lo, mid);//åˆ¤æ–­æ•°ç»„çš„å·¦è¾¹æ˜¯å¦æœ‰åº
        assert isSorted(a, mid + 1, hi);//åˆ¤æ–­æ•°ç»„çš„å³è¾¹æ˜¯å¦æœ‰åº

        for (int k = lo; k <= hi; k++) {
            aux[k] = a[k];
        }
        int i = lo, j = mid + 1;
        for (int k = lo; k <= hi; k++) {
            // å°†æ•°ç»„åˆ†ä¸ºä¸¤åŠï¼Œä¾æ¬¡æ¯”è¾ƒä¸¤ä¸ªæ•°ç»„çš„å…ƒç´ ï¼Œç„¶åå°†è¾ƒå°çš„ç»“æœå­˜å…¥åŸæ•°ç»„ä¸­
            if (i > mid)// å·¦è¾¹å–å®Œï¼Œç»§ç»­å–å³è¾¹çš„
                a[k] = aux[j++];
            else if (j > hi)// å³è¾¹å–å®Œï¼Œå–å·¦è¾¹çš„
                a[k] = aux[i++];
            else if (less(aux[j], aux[i]))// å½“å·¦å³ä¸¤è¾¹çš„æ•°ç»„éƒ½æ²¡æœ‰å–å®Œï¼Œä¸”å³è¾¹æ•°ç»„çš„å…ƒç´ è¾ƒå°ï¼Œå–å³è¾¹çš„
                a[k] = aux[j++];
            else// å¦åˆ™å–å·¦è¾¹çš„
                a[k] = aux[i++];
        }
        assert isSorted(a, lo, hi);
    }

    public static void sort(Comparable[] a) {
        Comparable[] aux = new Comparable[a.length];
        sort(a, aux, 0, a.length - 1);
        assert isSorted(a);

    }

    // è‡ªé¡¶å‘ä¸‹çš„å½’å¹¶æ’åº
    private static void sort(Comparable[] a, Comparable[] aux, int lo, int hi) {
        if (hi <= lo)
            return;
        int mid = lo + (hi - lo) / 2;
        sort(a, aux, lo, mid);// æ•°ç»„çš„å·¦åŠè¾¹æ’åºï¼Œé€’å½’
        sort(a, aux, mid + 1, hi);// æ•°ç»„çš„å³åŠè¾¹æ’åºï¼Œé€’å½’
        merge(a, aux, lo, mid, hi);// åŸåœ°å½’å¹¶
    }

    private static boolean less(Comparable a, Comparable b) {
        return a.compareTo(b) < 0;
    }

    private static boolean isSorted(Comparable[] a) {
        return isSorted(a, 0, a.length - 1);
    }

    private static boolean isSorted(Comparable[] a, int lo, int hi) {
        for (int i = lo + 1; i <= hi; i++) {
            if (less(a[i], a[i - 1]))
                return false;
        }
        return true;
    }
}
