public class KMP {
    private final int R;
    private int[][] dfa;//è®°å½•æ–‡æœ¬å’Œæ¨¡å¼ä¸åŒ¹é…æ—¶å€™ï¼Œæ¨¡å¼æŒ‡é’ˆé€€å›å¤šè¿œçš„å€¼

    private char[] pattern;
    private String pat;

    public KMP(String pat) {
        this.R = 256;
        this.pat = pat;

        int m = pat.length();
        dfa = new int[R][m];
        dfa[pat.charAt(0)][0] = 1;
        for (int x = 0, j = 1; j < m; j++) {
            for (int c = 0; c < R; c++) {
                //ä¸åŒ¹é… æ–‡æœ¬iä½ç½®çš„å¯ä»¥æ˜¯é™¤åŒ¹é…å­—ç¬¦ä»¥å¤–çš„ä»»æ„å­—ç¬¦ï¼Œä½†åœ¨æ­¤ä½ç½®çš„é‡å¯çŠ¶æ€Xéƒ½æ˜¯ä¸€æ ·çš„
                dfa[c][j] = dfa[c][x];
            }
            //åŒ¹é…,å°±æ˜¯è¯´åªæœ‰dfa[pat.charAt(j)][j]çš„é¡¹æ‰ä¼šä½¿çŠ¶æ€å‰è¿›ï¼Œä¸åŒ¹é…çš„éƒ½ä¼šç»é‡æ–°çŠ¶æ€Xè½¬åˆ°åˆé€‚çš„ä½ç½®
            dfa[pat.charAt(j)][j] = j + 1;
            x = dfa[pat.charAt(j)][x];//æ›´æ–°X ä¸åŒçŠ¶æ€ï¼Œå¯¹åº”çš„é‡å¯çŠ¶æ€Xä¸ä¸€æ ·
        }
    }

    public KMP(char[] pattern, int R) {
        this.R = R;
        this.pattern = new char[pattern.length];
        for (int j = 0; j < pattern.length; j++) {
            this.pattern[j] = pattern[j];
        }

        int m = pattern.length;
        dfa = new int[R][m];
        dfa[pattern[0]][0] = 1;
        for (int x = 0, j = 1; j < m; j++) {
            for (int c = 0; c < R; c++) {
                dfa[c][j] = dfa[c][x];//ä¸åŒ¹é…
            }
            dfa[pattern[j]][j] = j + 1;//åŒ¹é…
            x = dfa[pattern[j]][x];//æ¨¡å¼ä¸åŒä½ç½®ï¼Œå¯¹åº”çš„é‡å¯çŠ¶æ€Xä¸ä¸€æ ·
        }
    }

    public int search(String txt) {
        int m = pat.length();
        int n = txt.length();
        int i, j;
        for (i = 0, j = 0; i < n && j < m; i++) {
            //åªæœ‰dfa[txt.charAt(i)][j]==dfa[pat.charAt(j)][j],jæ‰ä¼šç­‰äºj+1,å³åŒ¹é…ã€‚å…¶ä»–çš„æƒ…å†µï¼Œjä¼šé€€å›åˆ°ä¹‹å‰çš„ä½ç½®
            j = dfa[txt.charAt(i)][j];
        }
        if (j == m) return i - m;//å‘ç°
        return n;//æœªå‘ç°
    }

    public int search(char[] text) {
        int m = pattern.length;
        int n = text.length;
        int i, j;
        for (i = 0, j = 0; i < n && j < m; i++) {
            j = dfa[text[i]][j];
        }
        if (j == m) return i - m;//å‘ç°
        return n;//æœªå‘ç°
    }

    public static void main(String[] args) {
        String pat = args[0];
        String txt = args[1];
        char[] pattern = pat.toCharArray();
        char[] text = txt.toCharArray();

        KMP kmp1 = new KMP(pat);
        int offset1 = kmp1.search(txt);

        KMP kmp2 = new KMP(pattern, 256);
        int offset2 = kmp2.search(text);

        StdOut.println("text:   " + txt);
        StdOut.print("pattern: ");
        for (int i = 0; i < offset1; i++)
            StdOut.print(" ");
        StdOut.println(pat);

        StdOut.print("pattern: ");
        for (int i = 0; i < offset2; i++)
            StdOut.println();
        StdOut.println(pat);
    }
}
